SELECT * FROM title AS t, char_name AS chn, cast_info AS ci, aka_name AS an WHERE ci.note IN ('(voice)', '(voice: Japanese version)', '(voice) (uncredited)', '(voice: English version)') AND t.production_year > 2000 AND t.id = ci.movie_id AND ci.movie_id = t.id AND ci.person_id = an.person_id AND an.person_id = ci.person_id AND chn.id = ci.person_role_id AND ci.person_role_id = chn.id;