SELECT * FROM company_name AS cn, info_type AS it, title AS t, movie_companies AS mc, movie_info AS mi WHERE cn.country_code = '[us]' AND it.info = 'release dates' AND t.production_year > 2000 AND t.id = mi.movie_id AND mi.movie_id = t.id AND t.id = mc.movie_id AND mc.movie_id = t.id AND mc.movie_id = mi.movie_id AND mi.movie_id = mc.movie_id AND cn.id = mc.company_id AND mc.company_id = cn.id AND it.id = mi.info_type_id AND mi.info_type_id = it.id;