SELECT * FROM movie_info_idx AS mi_idx, info_type AS it, movie_companies AS mc, company_type AS ct WHERE ct.kind = 'production companies' AND it.info = 'top 250 rank' AND mc.note NOT LIKE '%(as Metro-Goldwyn-Mayer Pictures)%' AND (mc.note LIKE '%(co-production)%' OR mc.note LIKE '%(presents)%') AND ct.id = mc.company_type_id AND mc.company_type_id = ct.id AND mc.movie_id = mi_idx.movie_id AND mi_idx.movie_id = mc.movie_id AND it.id = mi_idx.info_type_id AND mi_idx.info_type_id = it.id;