SELECT * FROM info_type AS it, movie_info_idx AS mi_idx, movie_companies AS mc WHERE it.info = 'bottom 10 rank' AND mc.note NOT LIKE '%(as Metro-Goldwyn-Mayer Pictures)%' AND mc.movie_id = mi_idx.movie_id AND mi_idx.movie_id = mc.movie_id AND it.id = mi_idx.info_type_id AND mi_idx.info_type_id = it.id;