SELECT * FROM movie_companies AS mc, company_type AS ct, movie_info_idx AS mi_idx WHERE ct.kind = 'production companies' AND mc.note NOT LIKE '%(as Metro-Goldwyn-Mayer Pictures)%' AND ct.id = mc.company_type_id AND mc.company_type_id = ct.id AND mc.movie_id = mi_idx.movie_id AND mi_idx.movie_id = mc.movie_id;