SELECT * FROM info_type AS it, title AS t, movie_companies AS mc, movie_info_idx AS mi_idx WHERE it.info = 'bottom 10 rank' AND mc.note NOT LIKE '%(as Metro-Goldwyn-Mayer Pictures)%' AND t.production_year > 2000 AND t.id = mc.movie_id AND mc.movie_id = t.id AND t.id = mi_idx.movie_id AND mi_idx.movie_id = t.id AND mc.movie_id = mi_idx.movie_id AND mi_idx.movie_id = mc.movie_id AND it.id = mi_idx.info_type_id AND mi_idx.info_type_id = it.id;