SELECT * FROM comp_cast_type AS cct1, complete_cast AS cc, comp_cast_type AS cct2, cast_info AS ci, name AS n WHERE cct1.kind = 'cast' AND cct2.kind LIKE '%complete%' AND n.name LIKE '%Downey%Robert%' AND ci.movie_id = cc.movie_id AND cc.movie_id = ci.movie_id AND n.id = ci.person_id AND ci.person_id = n.id AND cct1.id = cc.subject_id AND cc.subject_id = cct1.id AND cct2.id = cc.status_id AND cc.status_id = cct2.id;