SELECT * FROM comp_cast_type AS cct2, comp_cast_type AS cct1, complete_cast AS cc, title AS t WHERE cct1.kind = 'cast' AND cct2.kind LIKE '%complete%' AND t.production_year > 2000 AND t.id = cc.movie_id AND cc.movie_id = t.id AND cct1.id = cc.subject_id AND cc.subject_id = cct1.id AND cct2.id = cc.status_id AND cc.status_id = cct2.id;