SELECT * FROM comp_cast_type AS cct1, movie_keyword AS mk, complete_cast AS cc, kind_type AS kt, title AS t WHERE cct1.kind = 'cast' AND kt.kind = 'movie' AND t.production_year > 2000 AND kt.id = t.kind_id AND t.kind_id = kt.id AND t.id = mk.movie_id AND mk.movie_id = t.id AND t.id = cc.movie_id AND cc.movie_id = t.id AND mk.movie_id = cc.movie_id AND cc.movie_id = mk.movie_id AND cct1.id = cc.subject_id AND cc.subject_id = cct1.id;