SELECT * FROM movie_companies AS mc, title AS t, movie_info AS mi, company_type AS ct WHERE ct.kind = 'production companies' AND mc.note IS NULL AND mi.info IN ('Sweden', 'Norway', 'Germany', 'Denmark', 'Swedish', 'Denish', 'Norwegian', 'German') AND t.production_year BETWEEN 1950 AND 2000 AND t.id = mc.movie_id AND mc.movie_id = t.id AND mc.company_type_id = ct.id AND ct.id = mc.company_type_id AND mi.movie_id = t.id AND t.id = mi.movie_id AND mc.movie_id = mi.movie_id AND mi.movie_id = mc.movie_id;