SELECT * FROM movie_link AS ml, movie_companies AS mc, company_type AS ct, title AS t WHERE ct.kind = 'production companies' AND mc.note IS NULL AND t.production_year BETWEEN 1950 AND 2000 AND ml.movie_id = t.id AND t.id = ml.movie_id AND t.id = mc.movie_id AND mc.movie_id = t.id AND mc.company_type_id = ct.id AND ct.id = mc.company_type_id AND ml.movie_id = mc.movie_id AND mc.movie_id = ml.movie_id;