SELECT * FROM movie_link AS ml, title AS t, link_type AS lt, movie_info AS mi WHERE lt.link LIKE '%follow%' AND mi.info IN ('Sweden', 'Norway', 'Germany', 'Denmark', 'Swedish', 'Denish', 'Norwegian', 'German') AND t.production_year BETWEEN 1950 AND 2000 AND lt.id = ml.link_type_id AND ml.link_type_id = lt.id AND ml.movie_id = t.id AND t.id = ml.movie_id AND mi.movie_id = t.id AND t.id = mi.movie_id AND ml.movie_id = mi.movie_id AND mi.movie_id = ml.movie_id;