SELECT * FROM movie_companies AS mc, title AS t, movie_info AS mi WHERE mc.note IS NULL AND mi.info IN ('Sweden', 'Norway', 'Germany', 'Denmark', 'Swedish', 'Denish', 'Norwegian', 'German') AND t.production_year BETWEEN 1950 AND 2000 AND t.id = mc.movie_id AND mc.movie_id = t.id AND mi.movie_id = t.id AND t.id = mi.movie_id AND mc.movie_id = mi.movie_id AND mi.movie_id = mc.movie_id;