SELECT * FROM link_type AS lt, movie_link AS ml, movie_keyword AS mk, movie_info AS mi WHERE lt.link LIKE '%follow%' AND mi.info IN ('Germany', 'German') AND lt.id = ml.link_type_id AND ml.link_type_id = lt.id AND ml.movie_id = mk.movie_id AND mk.movie_id = ml.movie_id AND ml.movie_id = mi.movie_id AND mi.movie_id = ml.movie_id AND mk.movie_id = mi.movie_id AND mi.movie_id = mk.movie_id;