SELECT * FROM movie_keyword AS mk, keyword AS k, movie_info AS mi, title AS t WHERE k.keyword = 'sequel' AND mi.info IN ('Sweden', 'Norway', 'Germany', 'Denmark', 'Swedish', 'Denish', 'Norwegian', 'German', 'English') AND t.production_year BETWEEN 1950 AND 2010 AND t.id = mk.movie_id AND mk.movie_id = t.id AND mk.keyword_id = k.id AND k.id = mk.keyword_id AND mi.movie_id = t.id AND t.id = mi.movie_id AND mk.movie_id = mi.movie_id AND mi.movie_id = mk.movie_id;