SELECT * FROM movie_link AS ml, link_type AS lt, title AS t, movie_keyword AS mk WHERE lt.link LIKE '%follow%' AND t.production_year BETWEEN 1950 AND 2010 AND lt.id = ml.link_type_id AND ml.link_type_id = lt.id AND ml.movie_id = t.id AND t.id = ml.movie_id AND t.id = mk.movie_id AND mk.movie_id = t.id AND ml.movie_id = mk.movie_id AND mk.movie_id = ml.movie_id;