SELECT * FROM movie_info AS mi, info_type AS it1, movie_info_idx AS mi_idx WHERE it1.info = 'countries' AND mi.info IN ('Germany', 'German', 'USA', 'American') AND mi_idx.info < '7.0' AND mi.movie_id = mi_idx.movie_id AND mi_idx.movie_id = mi.movie_id AND it1.id = mi.info_type_id AND mi.info_type_id = it1.id;