SELECT * FROM movie_keyword AS mk, movie_info AS mi, info_type AS it1, title AS t WHERE it1.info = 'countries' AND mi.info IN ('Germany', 'German', 'USA', 'American') AND t.production_year > 2009 AND t.id = mi.movie_id AND mi.movie_id = t.id AND t.id = mk.movie_id AND mk.movie_id = t.id AND mk.movie_id = mi.movie_id AND mi.movie_id = mk.movie_id AND it1.id = mi.info_type_id AND mi.info_type_id = it1.id;