SELECT * FROM movie_keyword AS mk, title AS t, movie_info AS mi, kind_type AS kt WHERE kt.kind IN ('movie', 'episode') AND mi.info IN ('Germany', 'German', 'USA', 'American') AND t.production_year > 2009 AND kt.id = t.kind_id AND t.kind_id = kt.id AND t.id = mi.movie_id AND mi.movie_id = t.id AND t.id = mk.movie_id AND mk.movie_id = t.id AND mk.movie_id = mi.movie_id AND mi.movie_id = mk.movie_id;