SELECT * FROM keyword AS k, info_type AS it1, movie_keyword AS mk, movie_info AS mi, complete_cast AS cc, comp_cast_type AS cct1 WHERE cct1.kind = 'complete+verified' AND it1.info = 'release dates' AND mi.note LIKE '%internet%' AND mi.info IS NOT NULL AND (mi.info LIKE 'USA:% 199%' OR mi.info LIKE 'USA:% 200%') AND mk.movie_id = mi.movie_id AND mi.movie_id = mk.movie_id AND mk.movie_id = cc.movie_id AND cc.movie_id = mk.movie_id AND mi.movie_id = cc.movie_id AND cc.movie_id = mi.movie_id AND k.id = mk.keyword_id AND mk.keyword_id = k.id AND it1.id = mi.info_type_id AND mi.info_type_id = it1.id AND cct1.id = cc.status_id AND cc.status_id = cct1.id;