SELECT * FROM keyword AS k, complete_cast AS cc, comp_cast_type AS cct1, movie_info AS mi, movie_keyword AS mk WHERE cct1.kind = 'complete+verified' AND mi.note LIKE '%internet%' AND mi.info IS NOT NULL AND (mi.info LIKE 'USA:% 199%' OR mi.info LIKE 'USA:% 200%') AND mk.movie_id = mi.movie_id AND mi.movie_id = mk.movie_id AND mk.movie_id = cc.movie_id AND cc.movie_id = mk.movie_id AND mi.movie_id = cc.movie_id AND cc.movie_id = mi.movie_id AND k.id = mk.keyword_id AND mk.keyword_id = k.id AND cct1.id = cc.status_id AND cc.status_id = cct1.id;