SELECT * FROM movie_keyword AS mk, title AS t, complete_cast AS cc, comp_cast_type AS cct1 WHERE cct1.kind = 'complete+verified' AND t.production_year > 2000 AND t.id = mk.movie_id AND mk.movie_id = t.id AND t.id = cc.movie_id AND cc.movie_id = t.id AND mk.movie_id = cc.movie_id AND cc.movie_id = mk.movie_id AND cct1.id = cc.status_id AND cc.status_id = cct1.id;