SELECT * FROM company_name AS cn, movie_companies AS mc, movie_info AS mi, company_type AS ct, complete_cast AS cc, comp_cast_type AS cct1 WHERE cct1.kind = 'complete+verified' AND cn.country_code = '[us]' AND mi.note LIKE '%internet%' AND mi.info LIKE 'USA:% 200%' AND mi.movie_id = mc.movie_id AND mc.movie_id = mi.movie_id AND mi.movie_id = cc.movie_id AND cc.movie_id = mi.movie_id AND mc.movie_id = cc.movie_id AND cc.movie_id = mc.movie_id AND cn.id = mc.company_id AND mc.company_id = cn.id AND ct.id = mc.company_type_id AND mc.company_type_id = ct.id AND cct1.id = cc.status_id AND cc.status_id = cct1.id;