SELECT * FROM company_name AS cn, movie_companies AS mc, complete_cast AS cc, company_type AS ct WHERE cn.country_code = '[us]' AND mc.movie_id = cc.movie_id AND cc.movie_id = mc.movie_id AND cn.id = mc.company_id AND mc.company_id = cn.id AND ct.id = mc.company_type_id AND mc.company_type_id = ct.id;