SELECT * FROM company_name AS cn, movie_companies AS mc, company_type AS ct, kind_type AS kt, title AS t WHERE cn.country_code = '[us]' AND kt.kind IN ('movie') AND t.production_year > 2000 AND kt.id = t.kind_id AND t.kind_id = kt.id AND t.id = mc.movie_id AND mc.movie_id = t.id AND cn.id = mc.company_id AND mc.company_id = cn.id AND ct.id = mc.company_type_id AND mc.company_type_id = ct.id;