SELECT * FROM company_type AS ct, movie_companies AS mc, company_name AS cn, complete_cast AS cc, comp_cast_type AS cct1 WHERE cct1.kind = 'complete+verified' AND cn.country_code = '[us]' AND mc.movie_id = cc.movie_id AND cc.movie_id = mc.movie_id AND cn.id = mc.company_id AND mc.company_id = cn.id AND ct.id = mc.company_type_id AND mc.company_type_id = ct.id AND cct1.id = cc.status_id AND cc.status_id = cct1.id;