SELECT * FROM title AS t, movie_companies AS mc, complete_cast AS cc, company_name AS cn WHERE cn.country_code = '[us]' AND t.production_year > 1990 AND t.id = mc.movie_id AND mc.movie_id = t.id AND t.id = cc.movie_id AND cc.movie_id = t.id AND mc.movie_id = cc.movie_id AND cc.movie_id = mc.movie_id AND cn.id = mc.company_id AND mc.company_id = cn.id;