SELECT * FROM cast_info AS ci, aka_name AS an, movie_keyword AS mk, keyword AS k WHERE ci.note IN ('(voice)', '(voice: Japanese version)', '(voice) (uncredited)', '(voice: English version)') AND k.keyword IN ('hero', 'martial-arts', 'hand-to-hand-combat') AND ci.movie_id = mk.movie_id AND mk.movie_id = ci.movie_id AND ci.person_id = an.person_id AND an.person_id = ci.person_id AND k.id = mk.keyword_id AND mk.keyword_id = k.id;