SELECT * FROM movie_companies AS mc, cast_info AS ci, role_type AS rt WHERE ci.note IN ('(voice)', '(voice: Japanese version)', '(voice) (uncredited)', '(voice: English version)') AND rt.role = 'actress' AND mc.movie_id = ci.movie_id AND ci.movie_id = mc.movie_id AND rt.id = ci.role_id AND ci.role_id = rt.id;