SELECT * FROM keyword AS k, company_name AS cn, movie_keyword AS mk, movie_companies AS mc WHERE cn.country_code = '[us]' AND k.keyword IN ('hero', 'martial-arts', 'hand-to-hand-combat') AND mc.movie_id = mk.movie_id AND mk.movie_id = mc.movie_id AND cn.id = mc.company_id AND mc.company_id = cn.id AND k.id = mk.keyword_id AND mk.keyword_id = k.id;