SELECT * FROM title AS t, info_type AS it, movie_info AS mi WHERE it.info = 'release dates' AND mi.info IS NOT NULL AND (mi.info LIKE 'Japan:%201%' OR mi.info LIKE 'USA:%201%') AND t.production_year > 2010 AND t.id = mi.movie_id AND mi.movie_id = t.id AND it.id = mi.info_type_id AND mi.info_type_id = it.id;