SELECT * FROM info_type AS it2, movie_info AS mi, movie_info_idx AS mi_idx WHERE it2.info = 'votes' AND mi.info IN ('Horror', 'Action', 'Sci-Fi', 'Thriller', 'Crime', 'War') AND mi.movie_id = mi_idx.movie_id AND mi_idx.movie_id = mi.movie_id AND it2.id = mi_idx.info_type_id AND mi_idx.info_type_id = it2.id;