SELECT * FROM cast_info AS ci, comp_cast_type AS cct1, info_type AS it2, movie_info_idx AS mi_idx, title AS t, kind_type AS kt, complete_cast AS cc WHERE cct1.kind = 'cast' AND it2.info = 'rating' AND kt.kind = 'movie' AND mi_idx.info > '7.0' AND t.production_year > 2000 AND kt.id = t.kind_id AND t.kind_id = kt.id AND t.id = ci.movie_id AND ci.movie_id = t.id AND t.id = cc.movie_id AND cc.movie_id = t.id AND t.id = mi_idx.movie_id AND mi_idx.movie_id = t.id AND ci.movie_id = cc.movie_id AND cc.movie_id = ci.movie_id AND ci.movie_id = mi_idx.movie_id AND mi_idx.movie_id = ci.movie_id AND cc.movie_id = mi_idx.movie_id AND mi_idx.movie_id = cc.movie_id AND cct1.id = cc.subject_id AND cc.subject_id = cct1.id AND it2.id = mi_idx.info_type_id AND mi_idx.info_type_id = it2.id;