SELECT * FROM cast_info AS ci, movie_keyword AS mk, name AS n, complete_cast AS cc, comp_cast_type AS cct2 WHERE cct2.kind LIKE '%complete%' AND mk.movie_id = ci.movie_id AND ci.movie_id = mk.movie_id AND mk.movie_id = cc.movie_id AND cc.movie_id = mk.movie_id AND ci.movie_id = cc.movie_id AND cc.movie_id = ci.movie_id AND n.id = ci.person_id AND ci.person_id = n.id AND cct2.id = cc.status_id AND cc.status_id = cct2.id;