SELECT * FROM movie_info_idx AS mi_idx, cast_info AS ci, char_name AS chn, name AS n WHERE chn.name IS NOT NULL AND (chn.name LIKE '%man%' OR chn.name LIKE '%Man%') AND mi_idx.info > '7.0' AND ci.movie_id = mi_idx.movie_id AND mi_idx.movie_id = ci.movie_id AND chn.id = ci.person_role_id AND ci.person_role_id = chn.id AND n.id = ci.person_id AND ci.person_id = n.id;