SELECT * FROM movie_keyword AS mk, keyword AS k, complete_cast AS cc, comp_cast_type AS cct2 WHERE cct2.kind LIKE '%complete%' AND k.keyword IN ('superhero', 'marvel-comics', 'based-on-comic', 'fight') AND mk.movie_id = cc.movie_id AND cc.movie_id = mk.movie_id AND k.id = mk.keyword_id AND mk.keyword_id = k.id AND cct2.id = cc.status_id AND cc.status_id = cct2.id;