SELECT * FROM complete_cast AS cc, cast_info AS ci, char_name AS chn, movie_info_idx AS mi_idx WHERE chn.name IS NOT NULL AND (chn.name LIKE '%man%' OR chn.name LIKE '%Man%') AND mi_idx.info > '8.0' AND ci.movie_id = cc.movie_id AND cc.movie_id = ci.movie_id AND ci.movie_id = mi_idx.movie_id AND mi_idx.movie_id = ci.movie_id AND cc.movie_id = mi_idx.movie_id AND mi_idx.movie_id = cc.movie_id AND chn.id = ci.person_role_id AND ci.person_role_id = chn.id;