SELECT * FROM complete_cast AS cc, comp_cast_type AS cct2, info_type AS it2, movie_info_idx AS mi_idx, title AS t WHERE cct2.kind LIKE '%complete%' AND it2.info = 'rating' AND t.production_year > 2000 AND t.id = cc.movie_id AND cc.movie_id = t.id AND t.id = mi_idx.movie_id AND mi_idx.movie_id = t.id AND cc.movie_id = mi_idx.movie_id AND mi_idx.movie_id = cc.movie_id AND cct2.id = cc.status_id AND cc.status_id = cct2.id AND it2.id = mi_idx.info_type_id AND mi_idx.info_type_id = it2.id;