SELECT * FROM cast_info AS ci, movie_info_idx AS mi_idx, info_type AS it2, char_name AS chn WHERE chn.name IS NOT NULL AND (chn.name LIKE '%man%' OR chn.name LIKE '%Man%') AND it2.info = 'rating' AND ci.movie_id = mi_idx.movie_id AND mi_idx.movie_id = ci.movie_id AND chn.id = ci.person_role_id AND ci.person_role_id = chn.id AND it2.id = mi_idx.info_type_id AND mi_idx.info_type_id = it2.id;