SELECT * FROM comp_cast_type AS cct2, movie_keyword AS mk, complete_cast AS cc, comp_cast_type AS cct1, title AS t, keyword AS k WHERE cct1.kind IN ('cast', 'crew') AND cct2.kind = 'complete' AND k.keyword = 'sequel' AND t.production_year BETWEEN 1950 AND 2000 AND t.id = mk.movie_id AND mk.movie_id = t.id AND mk.keyword_id = k.id AND k.id = mk.keyword_id AND t.id = cc.movie_id AND cc.movie_id = t.id AND cct1.id = cc.subject_id AND cc.subject_id = cct1.id AND cct2.id = cc.status_id AND cc.status_id = cct2.id AND mk.movie_id = cc.movie_id AND cc.movie_id = mk.movie_id;