SELECT * FROM keyword AS k, movie_info AS mi, movie_keyword AS mk, title AS t WHERE k.keyword = 'sequel' AND mi.info IN ('Sweden', 'Germany', 'Swedish', 'German') AND t.production_year = 1998 AND t.id = mk.movie_id AND mk.movie_id = t.id AND mk.keyword_id = k.id AND k.id = mk.keyword_id AND mi.movie_id = t.id AND t.id = mi.movie_id AND mk.movie_id = mi.movie_id AND mi.movie_id = mk.movie_id;