SELECT * FROM complete_cast AS cc, link_type AS lt, movie_link AS ml, comp_cast_type AS cct2, comp_cast_type AS cct1, title AS t, movie_info AS mi WHERE cct1.kind = 'cast' AND cct2.kind LIKE 'complete%' AND lt.link LIKE '%follow%' AND mi.info IN ('Sweden', 'Norway', 'Germany', 'Denmark', 'Swedish', 'Denish', 'Norwegian', 'German', 'English') AND t.production_year BETWEEN 1950 AND 2010 AND lt.id = ml.link_type_id AND ml.link_type_id = lt.id AND ml.movie_id = t.id AND t.id = ml.movie_id AND mi.movie_id = t.id AND t.id = mi.movie_id AND t.id = cc.movie_id AND cc.movie_id = t.id AND cct1.id = cc.subject_id AND cc.subject_id = cct1.id AND cct2.id = cc.status_id AND cc.status_id = cct2.id AND ml.movie_id = mi.movie_id AND mi.movie_id = ml.movie_id AND ml.movie_id = cc.movie_id AND cc.movie_id = ml.movie_id AND mi.movie_id = cc.movie_id AND cc.movie_id = mi.movie_id;