SELECT * FROM complete_cast AS cc, comp_cast_type AS cct1, comp_cast_type AS cct2, title AS t, movie_info AS mi WHERE cct1.kind = 'cast' AND cct2.kind LIKE 'complete%' AND mi.info IN ('Sweden', 'Norway', 'Germany', 'Denmark', 'Swedish', 'Denish', 'Norwegian', 'German', 'English') AND t.production_year BETWEEN 1950 AND 2010 AND mi.movie_id = t.id AND t.id = mi.movie_id AND t.id = cc.movie_id AND cc.movie_id = t.id AND cct1.id = cc.subject_id AND cc.subject_id = cct1.id AND cct2.id = cc.status_id AND cc.status_id = cct2.id AND mi.movie_id = cc.movie_id AND cc.movie_id = mi.movie_id;