SELECT * FROM company_type AS ct, movie_companies AS mc, complete_cast AS cc, company_name AS cn WHERE cn.country_code != '[pl]' AND (cn.name LIKE '%Film%' OR cn.name LIKE '%Warner%') AND ct.kind = 'production companies' AND mc.note IS NULL AND mc.company_type_id = ct.id AND ct.id = mc.company_type_id AND mc.company_id = cn.id AND cn.id = mc.company_id AND mc.movie_id = cc.movie_id AND cc.movie_id = mc.movie_id;