SELECT * FROM company_name AS cn, movie_companies AS mc, movie_info AS mi WHERE cn.country_code != '[pl]' AND (cn.name LIKE '%Film%' OR cn.name LIKE '%Warner%') AND mc.note IS NULL AND mi.info IN ('Sweden', 'Norway', 'Germany', 'Denmark', 'Swedish', 'Denish', 'Norwegian', 'German', 'English') AND mc.company_id = cn.id AND cn.id = mc.company_id AND mc.movie_id = mi.movie_id AND mi.movie_id = mc.movie_id;