SELECT * FROM company_name AS cn, comp_cast_type AS cct2, movie_companies AS mc, complete_cast AS cc, company_type AS ct WHERE cct2.kind != 'complete+verified' AND cn.country_code != '[us]' AND mc.note NOT LIKE '%(USA)%' AND mc.note LIKE '%(200%)%' AND mc.movie_id = cc.movie_id AND cc.movie_id = mc.movie_id AND ct.id = mc.company_type_id AND mc.company_type_id = ct.id AND cn.id = mc.company_id AND mc.company_id = cn.id AND cct2.id = cc.status_id AND cc.status_id = cct2.id;