SELECT * FROM aka_name AS an, keyword AS k, role_type AS rt, cast_info AS ci, movie_keyword AS mk, info_type AS it3, person_info AS pi WHERE ci.note IN ('(voice)', '(voice) (uncredited)', '(voice: English version)') AND it3.info = 'trivia' AND k.keyword = 'computer-animation' AND rt.role = 'actress' AND ci.movie_id = mk.movie_id AND mk.movie_id = ci.movie_id AND rt.id = ci.role_id AND ci.role_id = rt.id AND ci.person_id = an.person_id AND an.person_id = ci.person_id AND ci.person_id = pi.person_id AND pi.person_id = ci.person_id AND it3.id = pi.info_type_id AND pi.info_type_id = it3.id AND k.id = mk.keyword_id AND mk.keyword_id = k.id;