SELECT * FROM person_info AS pi, cast_info AS ci, aka_name AS an, movie_keyword AS mk, keyword AS k WHERE ci.note IN ('(voice)', '(voice) (uncredited)', '(voice: English version)') AND k.keyword = 'computer-animation' AND ci.movie_id = mk.movie_id AND mk.movie_id = ci.movie_id AND ci.person_id = an.person_id AND an.person_id = ci.person_id AND ci.person_id = pi.person_id AND pi.person_id = ci.person_id AND k.id = mk.keyword_id AND mk.keyword_id = k.id;