SELECT * FROM comp_cast_type AS cct2, complete_cast AS cc, title AS t, cast_info AS ci WHERE cct2.kind = 'complete+verified' AND ci.note IN ('(voice)', '(voice) (uncredited)', '(voice: English version)') AND t.title = 'Shrek 2' AND t.production_year BETWEEN 2000 AND 2010 AND t.id = ci.movie_id AND ci.movie_id = t.id AND t.id = cc.movie_id AND cc.movie_id = t.id AND ci.movie_id = cc.movie_id AND cc.movie_id = ci.movie_id AND cct2.id = cc.status_id AND cc.status_id = cct2.id;