SELECT * FROM comp_cast_type AS cct1, complete_cast AS cc, cast_info AS ci, aka_name AS an, movie_info AS mi WHERE cct1.kind = 'cast' AND ci.note IN ('(voice)', '(voice) (uncredited)', '(voice: English version)') AND mi.info LIKE 'USA:%200%' AND mi.movie_id = ci.movie_id AND ci.movie_id = mi.movie_id AND mi.movie_id = cc.movie_id AND cc.movie_id = mi.movie_id AND ci.movie_id = cc.movie_id AND cc.movie_id = ci.movie_id AND ci.person_id = an.person_id AND an.person_id = ci.person_id AND cct1.id = cc.subject_id AND cc.subject_id = cct1.id;